mapper.select:
/* mngMemberUserMngMapper.UserList */ SELECT * FROM ( SELECT Row_number() OVER(ORDER BY updateDatetime DESC) AS rownum,* FROM( SELECT Seq AS seq ,Id AS id ,(case when Type='d' then '직조직' when Type='p' then '파트너' end) AS type ,Password AS password ,Name AS name ,Email AS email ,TelNo AS telNo ,RegistNo AS registNo ,Department AS department ,(SELECt name from CIS_UserAgencyDepartment UD where (UseYn IN (N'y')) AND UD.code = A.department) department_name ,RepresentativeName AS representativeName ,GroupCode AS groupCode ,(SELECT name FROM CIS_UserGroup UG WHERE UG.CODE =A.GroupCode) AS groupName ,EnableYn AS enableYn ,UseYn AS useYn ,RegistUser AS registUser ,RegistDatetime AS registDatetime ,UpdateUser AS updateUser ,UpdateDatetime AS updateDatetime FROM CIS_User A WHERE (groupCode != '00000001' AND groupCode != '00000002')
and type = #{type}
and useYn = #{useYn}
and groupCode = #{groupCode}
) AS T1 WHERE 1=1
/* 특수문자 조회를 위해 ESCAPE 추가*/ t1.id like '%'+#{item.srchKeyword}+'%' ESCAPE '['
t1.name like '%'+#{item.srchKeyword}+'%'
t1.department_name like '%'+#{item.srchKeyword}+'%'
(t1.id like '%'+#{item.srchKeyword}+'%' ESCAPE '[' OR t1.name like '%'+#{item.srchKeyword}+'%' OR t1.department_name like '%'+#{item.srchKeyword}+'%')
) AS T2 WHERE rownum BETWEEN ((#{pageIndex}-1)*#{recordCountPerPage})+1 AND (#{pageIndex}*#{recordCountPerPage})

/* mngMemberUserMngMapper.UserListTotCnt */ SELECT COUNT(*) AS totcnt FROM ( SELECT Row_number() OVER(ORDER BY SEQ DESC) AS rownum ,Seq AS seq ,Id AS id ,(case when Type='d' then '직조직' when Type='p' then '파트너' end) AS type ,Password AS password ,Name AS name ,Email AS email ,TelNo AS telNo ,RegistNo AS registNo ,Department AS department ,(SELECt name from CIS_UserAgencyDepartment UD where (UseYn IN (N'y')) AND UD.code = A.department) department_name ,RepresentativeName AS representativeName ,GroupCode AS groupCode ,(SELECT name FROM CIS_UserGroup UG WHERE UG.CODE =A.GroupCode) AS groupName ,EnableYn AS enableYn ,UseYn AS useYn ,RegistUser AS registUser ,RegistDatetime AS registDatetime ,UpdateUser AS updateUser ,UpdateDatetime AS updateDatetime FROM CIS_User A WHERE (groupCode != '00000001' AND groupCode != '00000002')
and type = #{type}
and useYn = #{useYn}
and groupCode = #{groupCode}
) AS T1 where 1=1
/* 특수문자 조회를 위해 ESCAPE 추가*/ T1.id like '%'+#{item.srchKeyword}+'%' ESCAPE '['
T1.name like '%'+#{item.srchKeyword}+'%'
T1.department_name like '%'+#{item.srchKeyword}+'%'
(T1.id like '%'+#{item.srchKeyword}+'%' ESCAPE '[' OR T1.name like '%'+#{item.srchKeyword}+'%' OR T1.department_name like '%'+#{item.srchKeyword}+'%')

/* mngMemberUserMngMapper.UserMngList */ SELECT * FROM ( SELECT Row_number() OVER(ORDER BY updateDatetime DESC) AS rownum,* FROM( SELECT Seq AS seq ,Id AS id ,(case when Type='d' then '직조직' when Type='p' then '파트너' end) AS type ,Password AS password ,Name AS name ,Email AS email ,TelNo AS telNo ,RegistNo AS registNo ,Department AS department ,(SELECt name from CIS_UserAgencyDepartment UD where ([Type] IN (N'd')) AND (UseYn IN (N'y')) AND UD.code = A.department) department_name ,RepresentativeName AS representativeName ,GroupCode AS groupCode ,(SELECT name FROM CIS_UserGroup UG WHERE UG.CODE =A.GroupCode) AS groupName ,EnableYn AS enableYn ,UseYn AS useYn ,RegistUser AS registUser ,RegistDatetime AS registDatetime ,UpdateUser AS updateUser ,UpdateDatetime AS updateDatetime FROM CIS_User A WHERE Type='d' AND (groupCode = '00000001' or groupCode = '00000002')
and useYn = #{useYn}
and groupCode = #{groupCode}
) AS T1 WHERE 1=1
/* 특수문자 조회를 위해 ESCAPE 추가*/ T1.id like '%'+#{item.srchKeyword}+'%' ESCAPE '['
T1.name like '%'+#{item.srchKeyword}+'%'
T1.department_name like '%'+#{item.srchKeyword}+'%'
(T1.id like '%'+#{item.srchKeyword}+'%' ESCAPE '[' OR T1.name like '%'+#{item.srchKeyword}+'%' OR T1.department_name like '%'+#{item.srchKeyword}+'%')
) AS T2 WHERE rownum BETWEEN ((#{pageIndex}-1)*#{recordCountPerPage})+1 AND (#{pageIndex}*#{recordCountPerPage})

/* mngMemberUserMngMapper.UserMngListTotCnt */ SELECT COUNT(*) AS totcnt FROM ( SELECT Row_number() OVER(ORDER BY SEQ DESC) AS rownum ,Seq AS seq ,Id AS id ,(case when Type='d' then '직조직' when Type='p' then '파트너' end) AS type ,Password AS password ,Name AS name ,Email AS email ,TelNo AS telNo ,RegistNo AS registNo ,Department AS department ,(SELECt name from CIS_UserAgencyDepartment UD where ([Type] IN (N'd')) AND (UseYn IN (N'y')) AND UD.code = A.department) department_name ,RepresentativeName AS representativeName ,GroupCode AS groupCode ,(SELECT name FROM CIS_UserGroup UG WHERE UG.CODE =A.GroupCode) AS groupName ,EnableYn AS enableYn ,UseYn AS useYn ,RegistUser AS registUser ,RegistDatetime AS registDatetime ,UpdateUser AS updateUser ,UpdateDatetime AS updateDatetime FROM CIS_User A WHERE Type='d' AND (groupCode = '00000001' or groupCode = '00000002')
and useYn = #{useYn}
and groupCode = #{groupCode}
) AS T1 WHERE 1=1
/* 특수문자 조회를 위해 ESCAPE 추가*/ AND T1.id like '%'+#{item.srchKeyword}+'%' ESCAPE '['
AND T1.name like '%'+#{item.srchKeyword}+'%'
AND T1.department_name like '%'+#{item.srchKeyword}+'%'
AND (T1.id like '%'+#{item.srchKeyword}+'%' ESCAPE '[' OR T1.name like '%'+#{item.srchKeyword}+'%' OR T1.department_name like '%'+#{item.srchKeyword}+'%')

        /* mngMemberUserMngMapper.UserAgencyDepartmentList */

		SELECT COUNT(*) AS totcnt
		FROM CIS_User
		WHERE Id = #{id}

        /* mngMemberUserMngMapper.UserAgencyDepartmentList */

		SELECT COUNT(*) AS totcnt
		FROM CIS_User
		WHERE Email = #{email}
